mapper.select:
select leave_id as leaveId, student_id as studentId, student_name as studentName, class_id as classId,professional_id as professionalId, colleage_id as colleageId, need_second_approve as needSecondApprove,instructor_approved as instructorApproved,student_pipe_approved as studentPipeApproved, approved as approved,instructor_note as instructorNote,student_pipe_note as studentPipeNote, leave_type as leaveType,course_index as courseIndex,teacher_name as teacherName, student_mobile as studentMobile,course_name as courseName,leave_days as leaveDays, student_note as studentNote,leave_start_date as leaveStartDate,leave_end_date as leaveEndDate, create_time as createTime,leave_sicked as leaveSicked,leave_sick_date as leaveSickDate, leave_date as leaveDate from tb_leave where 1=1
and student_id=#{studentId}
and approved=#{approved}
and approved in
#{item}
and leave_sicked = #{leaveSicked}
and create_time >= #{createTimeStart}
and #{createTimeEnd}>=create_time
order by create_time desc
limit #{begin},#{end}
limit 20

mapper.delete:
delete
from tb_leave
where student_id=#{userId} and leave_id=#{leaveId}

mapper.update:
update tb_leave
set leave_sicked=#{leaveSicked} , leave_sick_date=#{leaveSickDate}
 where leave_id=#{leaveId}

mapper.select:
select leave_id as leaveId, student_id as studentId, student_name as studentName, class_id as classId,professional_id as professionalId, colleage_id as colleageId, need_second_approve as needSecondApprove,instructor_approved as instructorApproved,student_pipe_approved as studentPipeApproved, approved as approved,instructor_note as instructorNote,student_pipe_note as studentPipeNote, leave_type as leaveType,course_index as courseIndex,teacher_name as teacherName, student_mobile as studentMobile,course_name as courseName,leave_days as leaveDays, student_note as studentNote,leave_start_date as leaveStartDate,leave_end_date as leaveEndDate, create_time as createTime,leave_sicked as leaveSicked,leave_sick_date as leaveSickDate, leave_date as leaveDate from tb_leave where 1=1
and class_id=#{classId}
and colleage_id=#{colleageId}
and class_id in
#{item}
and create_time>=#{beginTime}
and #{endTime}>=create_time
and leave_sicked=#{leaveSicked}
order by create_time desc
limit #{start},#{end}
limit 20

select leave_id as leaveId, student_id as studentId, student_name as studentName, class_id as classId,professional_id as professionalId, colleage_id as colleageId, need_second_approve as needSecondApprove,instructor_approved as instructorApproved,student_pipe_approved as studentPipeApproved, approved as approved,instructor_note as instructorNote,student_pipe_note as studentPipeNote, leave_type as leaveType,course_index as courseIndex,teacher_name as teacherName, student_mobile as studentMobile,course_name as courseName,leave_days as leaveDays, student_note as studentNote,leave_start_date as leaveStartDate,leave_end_date as leaveEndDate, create_time as createTime,leave_sicked as leaveSicked,leave_sick_date as leaveSickDate, leave_date as leaveDate from tb_leave where approved=1
and leave_sicked=#{leaveSicked}
and colleage_id =#{colleageId}
and class_id in
#{item.class_id}
and class_id =#{classId}
and leave_sick_date >#{leaveSickDateStart} and #{leaveSickDateEnd}>leave_sick_date
and #{leaveEndDate}>leave_end_date
and create_time>=#{createTimeStart}
and #{createTimeEnd}>=create_time
order by leave_sick_date desc
order by create_time desc
limit #{start},#{end}
limit 20
